import React, {useEffect, useState} from "react";
import {List, Avatar, Badge, Skeleton} from 'antd';
import { MessageOutlined, LikeOutlined } from '@ant-design/icons';
import IconText from "./IconText";
import {useDispatch, useSelector} from "react-redux";
import {RootState} from "../../reducers";
import {Link} from "react-router-dom";
import {GET_BOARD_ALL_REQUEST_ACTION} from "../../reducers/board";
import {marked} from 'marked';
// @ts-ignore
import PlainTextRenderer from "marked-plaintext";

const Board = () => {
    const {boards, common} = useSelector((state:RootState) => state.board);
    const [pageNumber, setPageNumber] = useState(0);
    const dispatch = useDispatch();
    const convertToPlainText = (markdownText: string) => {
        const plaintextOptions = {
            sanitize: false,
        }
        const renderer = new PlainTextRenderer();
        renderer.checkbox= (text: string) => {
            return text;
        }
        // @ts-ignore
        marked.setOptions(plaintextOptions);
        return marked(markdownText, {renderer})
    }


    useEffect(() => {
        dispatch(GET_BOARD_ALL_REQUEST_ACTION(pageNumber));
    }, [])

    return (
        <List
          itemLayout="vertical"
          pagination={{
            onChange: page => {
                setPageNumber(page);
            },
            pageSize: 5,
            current: pageNumber,
            size: 'small'
          }}
          dataSource={boards}
          renderItem={({avatar, contents, id, title, regDate, author}) => (
                <List.Item
                key={id}
                actions={ !common.boardLoading && [
                  <IconText icon={LikeOutlined} text="0" key="list-vertical-like-o" />,
                  <IconText icon={MessageOutlined} text="0" key="list-vertical-message" />,
                ]}
                extra={
                    !common.boardLoading &&
                  <img
                    width={272}
                    alt="logo"
                    src="data:image/png;base64,"
                  />
                }
              >
                <Skeleton loading={common.boardLoading} paragraph={{ rows: 3 }} active avatar>
                    <Badge.Ribbon text="새글" color="blue">
                    <List.Item.Meta
                        avatar={<Avatar src={avatar}>{author}</Avatar>}
                      title={
                        <Link to={`/board/${id}`}>
                          {title}
                        </Link>
                      }
                      description={regDate}
                    />
                        {
                            convertToPlainText(contents)
                        }
                    </Badge.Ribbon>
                </Skeleton>
            </List.Item>
          )}
        />
    )
}


export default Board;
